// Debug the success value
const input = $input.all()[0].json;

console.log('Full input:', JSON.stringify(input, null, 2));
console.log('Success value:', input.success);
console.log('Success type:', typeof input.success);
console.log('Success === true:', input.success === true);
console.log('Success == true:', input.success == true);

return [{
  json: {
    originalInput: input,
    successValue: input.success,
    successType: typeof input.success,
    strictEquality: input.success === true,
    looseEquality: input.success == true,
    shouldProceed: input.success === true
  }
}];
