// Simple validation check and routing
const input = $input.all()[0].json;

if (input.success === true) {
  // Success path - pass the data to API call
  return [input];
} else {
  // Error path - format error response  
  return [{
    json: {
      success: false,
      error: input.error || 'Validation failed'
    }
  }];
}
